import os__all__ = ['program']def program(cmd, skip_symlink = False, curr_dir=None, my_dir=None):	"""Return full path of swig or False	I don't check for executable bits"""		# Get current directory and script directory.	if curr_dir is None:		curr_dir = os.getcwd()		# Search path	path = [curr_dir]		# Lala	if my_dir is None:		try:			my_dir = os.path.dirname(__file__)			path.append(my_dir)		except NameError:			my_dir = None	else:		path.append(my_dir)		if 'PATH' in os.environ:		path.extend(os.environ['PATH'].split(os.pathsep))		path = os.path.expandvars(path)		for dir in path:		fullname = os.path.join(os.path.abspath(dir), cmd)		if skip_symlink and os.path.islink(fullname):			continue		elif os.path.isfile(fullname):			return fullname		# executable extension for NT		elif 'PATHEXT' in os.environ:			pathexts = os.environ['PATHEXT'].split(';')			for pathext in pathexts:				file_with_ext = fullname + pathext				if os.path.isfile (file_with_ext):					return file_with_ext	return False